mapper.select:
SELECT fileno, boardNo, path, original, stored, uploadTime
FROM files

	AND boardNo = #{boardNo}

SELECT fileno, boardNo, path, original, stored, uploadTime
FROM files

	AND fileno = #{fileno}

SELECT COUNT(*) FROM recommend

	AND boardNo = #{boardNo}
	AND memberno = #{memberno}

mapper.delete:
DELETE FROM recommend

	AND boardNo = #{boardNo}
	AND memberno = #{memberno}

mapper.select:
SELECT commentno, boardno, memberno, DBMS_LOB.SUBSTR(content) content, "date" FROM "Comment"

	AND boardNo = #{boardNo}

ORDER BY commentno ASC

SELECT COUNT(*) FROM COMMENTLIKE

	AND commentno = #{commentno}
	AND memberno = #{memberno}

mapper.delete:
DELETE FROM COMMENTLIKE

	AND commentno = #{commentno}
	AND memberno = #{memberno}

DELETE FROM "Comment"

	AND commentno = #{commentno}
	AND memberno = #{memberno}

mapper.select:
SELECT * FROM CONTENT

	AND memberno = #{memberno}

ORDER BY boardNo DESC

SELECT * FROM CONTENT

	AND memberno = #{memberno}
	AND boardNo = #{boardNo}
